{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"name":"python","version":"3.7.12","mimetype":"text/x-python","codemirror_mode":{"name":"ipython","version":3},"pygments_lexer":"ipython3","nbconvert_exporter":"python","file_extension":".py"}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T10:41:58.063587Z\",\"iopub.execute_input\":\"2022-07-19T10:41:58.064064Z\",\"iopub.status.idle\":\"2022-07-19T10:42:00.531720Z\",\"shell.execute_reply.started\":\"2022-07-19T10:41:58.063979Z\",\"shell.execute_reply\":\"2022-07-19T10:42:00.530266Z\"}}\nimport pandas as pd\nimport numpy as np\nimport os\nimport seaborn as sns\nimport matplotlib.pyplot as plt\nimport librosa\nimport librosa.display\nfrom IPython.display import Audio\nimport warnings\nwarnings.filterwarnings('ignore')\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:41.152718Z\",\"iopub.execute_input\":\"2022-07-19T11:21:41.153150Z\",\"iopub.status.idle\":\"2022-07-19T11:21:41.211170Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:41.153118Z\",\"shell.execute_reply\":\"2022-07-19T11:21:41.209873Z\"}}\npaths = []\nlabels = []\nfor dirname, _, filenames in os.walk('/kaggle/input'):\n    for filename in filenames:\n        paths.append(os.path.join(dirname, filename))\n        label = filename.split('_')[-1]\n        label = label.split('.')[0]\n        labels.append(label.lower())\nprint('Dataset is Loaded')\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:42.894976Z\",\"iopub.execute_input\":\"2022-07-19T11:21:42.895424Z\",\"iopub.status.idle\":\"2022-07-19T11:21:42.903855Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:42.895378Z\",\"shell.execute_reply\":\"2022-07-19T11:21:42.902343Z\"}}\npaths[:5]\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:43.802792Z\",\"iopub.execute_input\":\"2022-07-19T11:21:43.803919Z\",\"iopub.status.idle\":\"2022-07-19T11:21:43.812634Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:43.803874Z\",\"shell.execute_reply\":\"2022-07-19T11:21:43.811100Z\"}}\nlabels[:5]\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:44.480786Z\",\"iopub.execute_input\":\"2022-07-19T11:21:44.481456Z\",\"iopub.status.idle\":\"2022-07-19T11:21:44.506993Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:44.481335Z\",\"shell.execute_reply\":\"2022-07-19T11:21:44.504208Z\"}}\n## Create a dataframe\ndf = pd.DataFrame()\ndf['speech'] = paths\ndf['label'] = labels\ndf.head()\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:45.454286Z\",\"iopub.execute_input\":\"2022-07-19T11:21:45.454729Z\",\"iopub.status.idle\":\"2022-07-19T11:21:45.467002Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:45.454698Z\",\"shell.execute_reply\":\"2022-07-19T11:21:45.465577Z\"}}\ndf['label'].value_counts()\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:46.590279Z\",\"iopub.execute_input\":\"2022-07-19T11:21:46.590745Z\",\"iopub.status.idle\":\"2022-07-19T11:21:47.148354Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:46.590713Z\",\"shell.execute_reply\":\"2022-07-19T11:21:47.147006Z\"}}\nsns.countplot(df['label'])\n\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:47.226328Z\",\"iopub.execute_input\":\"2022-07-19T11:21:47.227192Z\",\"iopub.status.idle\":\"2022-07-19T11:21:47.237746Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:47.227147Z\",\"shell.execute_reply\":\"2022-07-19T11:21:47.236115Z\"}}\ndef waveshow(data, sr, emotion):\n    plt.figure(figsize=(10,4))\n    plt.title(emotion, size=20)\n    librosa.display.waveshow(data, sr=sr)\n    plt.show()\n    \ndef spectogram(data, sr, emotion):\n    x = librosa.stft(data)\n    xdb = librosa.amplitude_to_db(abs(x))\n    plt.figure(figsize=(11,4))\n    plt.title(emotion, size=20)\n    librosa.display.specshow(xdb, sr=sr, x_axis='time', y_axis='hz')\n    plt.colorbar()\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:47.760764Z\",\"iopub.execute_input\":\"2022-07-19T11:21:47.762307Z\",\"iopub.status.idle\":\"2022-07-19T11:21:48.956466Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:47.762256Z\",\"shell.execute_reply\":\"2022-07-19T11:21:48.954912Z\"}}\nemotion = 'fear'\npath = np.array(df['speech'][df['label']==emotion])[0]\ndata, sampling_rate = librosa.load(path)\nwaveshow(data, sampling_rate, emotion)\nspectogram(data, sampling_rate, emotion)\nAudio(path)\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:48.959418Z\",\"iopub.execute_input\":\"2022-07-19T11:21:48.962242Z\",\"iopub.status.idle\":\"2022-07-19T11:21:50.065405Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:48.962193Z\",\"shell.execute_reply\":\"2022-07-19T11:21:50.063893Z\"}}\nemotion = 'angry'\npath = np.array(df['speech'][df['label']==emotion])[1]\ndata, sampling_rate = librosa.load(path)\nwaveshow(data, sampling_rate, emotion)\nspectogram(data, sampling_rate, emotion)\nAudio(path)\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:50.068120Z\",\"iopub.execute_input\":\"2022-07-19T11:21:50.069105Z\",\"iopub.status.idle\":\"2022-07-19T11:21:51.227427Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:50.069042Z\",\"shell.execute_reply\":\"2022-07-19T11:21:51.225914Z\"}}\nemotion = 'disgust'\npath = np.array(df['speech'][df['label']==emotion])[0]\ndata, sampling_rate = librosa.load(path)\nwaveshow(data, sampling_rate, emotion)\nspectogram(data, sampling_rate, emotion)\nAudio(path)\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:51.231624Z\",\"iopub.execute_input\":\"2022-07-19T11:21:51.232512Z\",\"iopub.status.idle\":\"2022-07-19T11:21:52.363614Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:51.232462Z\",\"shell.execute_reply\":\"2022-07-19T11:21:52.362207Z\"}}\nemotion = 'neutral'\npath = np.array(df['speech'][df['label']==emotion])[0]\ndata, sampling_rate = librosa.load(path)\nwaveshow(data, sampling_rate, emotion)\nspectogram(data, sampling_rate, emotion)\nAudio(path)\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:52.366022Z\",\"iopub.execute_input\":\"2022-07-19T11:21:52.366518Z\",\"iopub.status.idle\":\"2022-07-19T11:21:53.504333Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:52.366460Z\",\"shell.execute_reply\":\"2022-07-19T11:21:53.502664Z\"}}\nemotion = 'sad'\npath = np.array(df['speech'][df['label']==emotion])[0]\ndata, sampling_rate = librosa.load(path)\nwaveshow(data, sampling_rate, emotion)\nspectogram(data, sampling_rate, emotion)\nAudio(path)\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:53.506299Z\",\"iopub.execute_input\":\"2022-07-19T11:21:53.509214Z\",\"iopub.status.idle\":\"2022-07-19T11:21:54.665428Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:53.509165Z\",\"shell.execute_reply\":\"2022-07-19T11:21:54.664093Z\"}}\nemotion = 'ps'\npath = np.array(df['speech'][df['label']==emotion])[0]\ndata, sampling_rate = librosa.load(path)\nwaveshow(data, sampling_rate, emotion)\nspectogram(data, sampling_rate, emotion)\nAudio(path)\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:54.668121Z\",\"iopub.execute_input\":\"2022-07-19T11:21:54.668989Z\",\"iopub.status.idle\":\"2022-07-19T11:21:56.069644Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:54.668945Z\",\"shell.execute_reply\":\"2022-07-19T11:21:56.068111Z\"}}\nemotion = 'happy'\npath = np.array(df['speech'][df['label']==emotion])[0]\ndata, sampling_rate = librosa.load(path)\nwaveshow(data, sampling_rate, emotion)\nspectogram(data, sampling_rate, emotion)\nAudio(path)\n\n# %% [markdown]\n# **FEATURE EXTRACTION**\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:56.075132Z\",\"iopub.execute_input\":\"2022-07-19T11:21:56.076003Z\",\"iopub.status.idle\":\"2022-07-19T11:21:56.085177Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:56.075919Z\",\"shell.execute_reply\":\"2022-07-19T11:21:56.083587Z\"}}\ndef extract_mfcc(filename):\n    y, sr = librosa.load(filename, duration=3, offset=0.5)\n    mfcc = np.mean(librosa.feature.mfcc(y=y, sr=sr, n_mfcc=40).T, axis=0)\n    return mfcc\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:56.087263Z\",\"iopub.execute_input\":\"2022-07-19T11:21:56.087827Z\",\"iopub.status.idle\":\"2022-07-19T11:21:56.458839Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:56.087765Z\",\"shell.execute_reply\":\"2022-07-19T11:21:56.456913Z\"}}\nextract_mfcc(df['speech'][0])\n\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:21:56.461712Z\",\"iopub.execute_input\":\"2022-07-19T11:21:56.462229Z\",\"iopub.status.idle\":\"2022-07-19T11:39:00.943482Z\",\"shell.execute_reply.started\":\"2022-07-19T11:21:56.462181Z\",\"shell.execute_reply\":\"2022-07-19T11:39:00.941465Z\"}}\nX_mfcc = df['speech'].apply(lambda x: extract_mfcc(x))\n\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:39:00.946141Z\",\"iopub.execute_input\":\"2022-07-19T11:39:00.946977Z\",\"iopub.status.idle\":\"2022-07-19T11:39:00.971171Z\",\"shell.execute_reply.started\":\"2022-07-19T11:39:00.946910Z\",\"shell.execute_reply\":\"2022-07-19T11:39:00.969759Z\"}}\nX_mfcc\n\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:39:00.973088Z\",\"iopub.execute_input\":\"2022-07-19T11:39:00.973989Z\",\"iopub.status.idle\":\"2022-07-19T11:39:00.990233Z\",\"shell.execute_reply.started\":\"2022-07-19T11:39:00.973897Z\",\"shell.execute_reply\":\"2022-07-19T11:39:00.988283Z\"}}\nX = [x for x in X_mfcc]\nX = np.array(X)\nX.shape\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:39:00.992481Z\",\"iopub.execute_input\":\"2022-07-19T11:39:00.993394Z\",\"iopub.status.idle\":\"2022-07-19T11:39:01.003295Z\",\"shell.execute_reply.started\":\"2022-07-19T11:39:00.993346Z\",\"shell.execute_reply\":\"2022-07-19T11:39:01.001639Z\"}}\n## input split\nX = np.expand_dims(X, -1)\nX.shape\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:39:01.010886Z\",\"iopub.execute_input\":\"2022-07-19T11:39:01.011641Z\",\"iopub.status.idle\":\"2022-07-19T11:39:01.028056Z\",\"shell.execute_reply.started\":\"2022-07-19T11:39:01.011598Z\",\"shell.execute_reply\":\"2022-07-19T11:39:01.026297Z\"}}\nfrom sklearn.preprocessing import OneHotEncoder\nenc = OneHotEncoder()\ny = enc.fit_transform(df[['label']])\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:39:01.030417Z\",\"iopub.execute_input\":\"2022-07-19T11:39:01.031261Z\",\"iopub.status.idle\":\"2022-07-19T11:39:01.038770Z\",\"shell.execute_reply.started\":\"2022-07-19T11:39:01.031201Z\",\"shell.execute_reply\":\"2022-07-19T11:39:01.036440Z\"}}\ny = y.toarray()\n\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:39:01.043568Z\",\"iopub.execute_input\":\"2022-07-19T11:39:01.045352Z\",\"iopub.status.idle\":\"2022-07-19T11:39:01.059880Z\",\"shell.execute_reply.started\":\"2022-07-19T11:39:01.045301Z\",\"shell.execute_reply\":\"2022-07-19T11:39:01.058501Z\"}}\ny.shape\n\n\n# %% [markdown]\n# **Create the LSTM Model**\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:39:01.061981Z\",\"iopub.execute_input\":\"2022-07-19T11:39:01.062366Z\",\"iopub.status.idle\":\"2022-07-19T11:39:01.434305Z\",\"shell.execute_reply.started\":\"2022-07-19T11:39:01.062336Z\",\"shell.execute_reply\":\"2022-07-19T11:39:01.432551Z\"}}\nfrom keras.models import Sequential\nfrom keras.layers import Dense, LSTM, Dropout\n\nmodel = Sequential([\n    LSTM(123, return_sequences=False, input_shape=(40,1)),\n    Dense(64, activation='relu'),\n    Dropout(0.2),\n    Dense(32, activation='relu'),\n    Dropout(0.2),\n    Dense(7, activation='softmax')\n])\n\nmodel.compile(loss='categorical_crossentropy', optimizer='adam', metrics=['accuracy'])\nmodel.summary()\n\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:39:01.436422Z\",\"iopub.execute_input\":\"2022-07-19T11:39:01.437234Z\",\"iopub.status.idle\":\"2022-07-19T11:39:17.942969Z\",\"shell.execute_reply.started\":\"2022-07-19T11:39:01.437186Z\",\"shell.execute_reply\":\"2022-07-19T11:39:17.941630Z\"}}\n# Train the model\nhistory = model.fit(X, y, validation_split=0.2, epochs=50, batch_size=64, shuffle=True)\n\n# %% [markdown]\n# **Plot the results**\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:39:17.944977Z\",\"iopub.execute_input\":\"2022-07-19T11:39:17.945311Z\",\"iopub.status.idle\":\"2022-07-19T11:39:18.555288Z\",\"shell.execute_reply.started\":\"2022-07-19T11:39:17.945281Z\",\"shell.execute_reply\":\"2022-07-19T11:39:18.553271Z\"}}\nepochs = list(range(100))\nacc = history.history['accuracy']\nval_acc = history.history['val_accuracy']\n\nplt.plot(epochs, acc, label='train accuracy')\nplt.plot(epochs, val_acc, label='val accuracy')\nplt.xlabel('epochs')\nplt.ylabel('accuracy')\nplt.legend()\nplt.show()\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2022-07-19T11:39:44.143701Z\",\"iopub.execute_input\":\"2022-07-19T11:39:44.144252Z\",\"iopub.status.idle\":\"2022-07-19T11:39:44.365002Z\",\"shell.execute_reply.started\":\"2022-07-19T11:39:44.144188Z\",\"shell.execute_reply\":\"2022-07-19T11:39:44.362878Z\"}}\nloss = history.history['loss']\nval_loss = history.history['val_loss']\n\nplt.plot(epochs, loss, label='train loss')\nplt.plot(epochs, val_loss, label='val loss')\nplt.xlabel('epochs')\nplt.ylabel('loss')\nplt.legend()\nplt.show()\n\n# %% [code]\n","metadata":{"_uuid":"f037e321-9fd0-4ce7-9257-c6c371b432ed","_cell_guid":"6227c639-8101-4cc5-bf5c-0e7902de928e","collapsed":false,"jupyter":{"outputs_hidden":false},"execution":{"iopub.status.busy":"2022-07-19T13:04:55.668952Z","iopub.execute_input":"2022-07-19T13:04:55.669400Z","iopub.status.idle":"2022-07-19T13:33:00.748988Z","shell.execute_reply.started":"2022-07-19T13:04:55.669358Z","shell.execute_reply":"2022-07-19T13:33:00.747509Z"},"trusted":true},"execution_count":2,"outputs":[]}]}